import React, {useState, useMemo, useEffect} from 'react';
import Header from './components/Header';
import TaskList from './components/TaskList';
import SkillPlanner from './components/SkillPlanner';
import Analytics from './components/Analytics';
import DateBox from './components/DateBox';
import './styles.css';
import { format } from 'date-fns';
import { loadTasks, saveTasks, loadSkills, saveSkills } from './utils/storage';

const makeId = ()=>Math.random().toString(36).slice(2,9);

export default function App(){
  const [view,setView] = useState('daily');
  const [tasks,setTasks] = useState(()=>{
    const stored = loadTasks();
    if(stored) return stored;
    return [
      {id:makeId(),title:'Morning run',description:'30 minutes',priority:'High',date:format(new Date(),'yyyy-MM-dd'),status:'pending'},
      {id:makeId(),title:'Read 20 pages',description:'Skill: Reading',priority:'Medium',date:format(new Date(),'yyyy-MM-dd'),status:'pending'}
    ];
  });
  const [skills,setSkills] = useState(()=>{
    const stored = loadSkills();
    return stored || [{id:makeId(),name:'React'},{id:makeId(),name:'Spanish'}];
  });

  const addTask = (data)=>{
    const t = {id:makeId(),title:data.title,description:data.description,priority:data.priority,date:data.date,status:'pending'};
    setTasks(s=>[t,...s]);
  }
  const toggleTask = (id)=>{
    setTasks(s=>s.map(t=>t.id===id?{...t,status: t.status==='completed'?'pending':'completed'}:t));
  }
  const failTask = (id)=>{
    setTasks(s=>s.map(t=>t.id===id?{...t,status: t.status==='missed'?'pending':'missed'}:t));
  }
  const deleteTask = (id)=> setTasks(s=>s.filter(t=>t.id!==id));
  const editTask = (id)=>{
    const t = tasks.find(x=>x.id===id);
    if(!t) return;
    const title = prompt('Title', t.title);
    if(title===null) return;
    const description = prompt('Description', t.description||'');
    setTasks(s=>s.map(x=>x.id===id?{...x,title,description}:x));
  }

  const addSkill = ({name})=> setSkills(s=>[ {id:makeId(),name}, ...s]);
  const reorderSkill = (id,direction)=>{
    setSkills(s=>{
      const idx = s.findIndex(x=>x.id===id);
      if(idx<0) return s;
      const swap = direction==='up'? idx-1: idx+1;
      if(swap<0||swap>=s.length) return s;
      const arr = [...s];
      const tmp = arr[swap]; arr[swap]=arr[idx]; arr[idx]=tmp;
      return arr;
    })
  }

  // persist
  useEffect(()=> saveTasks(tasks),[tasks]);
  useEffect(()=> saveSkills(skills),[skills]);

  const handleExport = ()=>{
    const data = {tasks,skills,exportedAt: new Date().toISOString()};
    const blob = new Blob([JSON.stringify(data, null, 2)], {type:'application/json'});
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url; a.download = 'goal-planner-export.json';
    a.click();
    URL.revokeObjectURL(url);
  }

  const handleImport = (e)=>{
    const f = e.target.files && e.target.files[0];
    if(!f) return;
    const reader = new FileReader();
    reader.onload = ()=>{
      try{
        const parsed = JSON.parse(reader.result);
        if(parsed.tasks) setTasks(parsed.tasks);
        if(parsed.skills) setSkills(parsed.skills);
      }catch(err){ alert('Invalid file') }
    };
    reader.readAsText(f);
  }

  const dates = useMemo(()=>{
    // show a short range for daily view
    if(view==='daily'){
      const d = new Date();
      return [format(d,'yyyy-MM-dd')];
    }
    if(view==='monthly'){
      // show first 7 days sample
      const out = [];
      for(let i=1;i<=7;i++){
        const dt = new Date(); dt.setDate(i);
        out.push(format(dt,'yyyy-MM-dd'))
      }
      return out;
    }
    // yearly: show sample first of months
    const out = [];
    for(let m=0;m<6;m++){
      const dt = new Date(); dt.setMonth(m); dt.setDate(1);
      out.push(format(dt,'yyyy-MM-dd'))
    }
    return out;
  },[view]);

  const tasksByDate = (date)=> tasks.filter(t=>t.date===date);

  return (
    <div className="container">
      <Header 
        view={view} 
        setView={setView} 
        onExport={handleExport} 
        onImport={handleImport} 
      />
      <div className="grid">
        <main>
          <TaskList 
            tasks={tasks} 
            onAdd={addTask} 
            onToggle={toggleTask} 
            onFail={failTask} 
            onDelete={deleteTask} 
            onEdit={editTask} 
            view={view} 
          />

          <div className="date-section">
            <h3 className="mb-4">Timeline View</h3>
            {dates.map(d=> (
              <DateBox key={d} date={d} tasks={tasksByDate(d)} />
            ))}
          </div>
        </main>

        <aside>
          <SkillPlanner skills={skills} onAdd={addSkill} onSetPriority={(id,dir)=>reorderSkill(id,dir)} />
          <div style={{height:24}} />
          <Analytics tasks={tasks} />
        </aside>
      </div>
    </div>
  )
}
