import React from 'react';
import { Bar, Pie } from 'react-chartjs-2';
import { Chart, ArcElement, BarElement, CategoryScale, LinearScale, Tooltip, Legend } from 'chart.js';
Chart.register(ArcElement,BarElement,CategoryScale,LinearScale,Tooltip,Legend);

function aggregate(tasks){
  const total = tasks.length;
  const completed = tasks.filter(t=>t.status==='completed').length;
  const missed = tasks.filter(t=>t.status==='missed').length;
  const pending = total - completed - missed;
  const percent = total? Math.round((completed/total)*100):0;
  return {total,completed,missed,pending,percent};
}

export default function Analytics({tasks}){
  const agg = aggregate(tasks);
  const pieData = {
    labels:['Completed','Missed','Pending'],
    datasets:[{data:[agg.completed,agg.missed,agg.pending],backgroundColor:['#10b981','#ef4444','#f59e0b']}]
  }
  const barData = {
    labels:['Total','Completed','Missed'],
    datasets:[{label:'Tasks',data:[agg.total,agg.completed,agg.missed],backgroundColor:['#3b82f6','#10b981','#ef4444']}]
  }

  return (
    <div className="card">
      <h3>Analytics</h3>
      <div style={{display:'flex',gap:12,alignItems:'center',flexWrap:'wrap'}}>
        <div style={{width:220}}>
          <Pie data={pieData} />
        </div>
        <div style={{flex:1}}>
          <Bar data={barData} />
          <div style={{marginTop:8}}>
            <div className="muted">Completion: {agg.percent}%</div>
            <div className="muted">Total: {agg.total} • Completed: {agg.completed} • Missed: {agg.missed}</div>
          </div>
        </div>
      </div>
    </div>
  )
}
