import React from 'react';
import { displayDate } from '../utils/dateUtils';

export default function DateBox({date,tasks}){
  return (
    <div className="date-box card">
      <strong>{displayDate(date)}</strong>
      <div style={{marginTop:8}}>
        {tasks.length===0 && <div className="muted">No tasks planned</div>}
        {tasks.map(t=> (
          <div key={t.id} style={{display:'flex',justifyContent:'space-between',padding:'6px 0'}}>
            <div>
              <div>{t.title} <span className={`muted`}>[{t.priority}]</span></div>
              <div className="muted">{t.description}</div>
            </div>
            <div style={{alignSelf:'center'}}>
              {t.status==='completed' ? '✔' : t.status==='missed' ? '✖' : '—'}
            </div>
          </div>
        ))}
      </div>
    </div>
  )
}
