import React, { useContext, useState, useMemo } from 'react';
import { AppContext } from '../context/AppContext';
import { GradientCard, PriorityBadge, EmptyState } from './UIComponents';
import { format, getMonth, getYear } from 'date-fns';

const MonthlyTasksPage = () => {
  const {
    tasks,
    toggleTask,
    markTaskMissed,
    deleteTask,
    addTask,
    getTasksByMonth,
  } = useContext(AppContext);

  const [selectedDate, setSelectedDate] = useState(new Date());
  const [searchTerm, setSearchTerm] = useState('');
  const [priorityFilter, setPriorityFilter] = useState('All');
  const [showTaskModal, setShowTaskModal] = useState(false);
  const [expandedDates, setExpandedDates] = useState({});
  const [taskForm, setTaskForm] = useState({
    title: '',
    description: '',
    priority: 'Medium',
    date: format(new Date(), 'yyyy-MM-dd'),
  });

  const currentMonth = getMonth(selectedDate) + 1;
  const currentYear = getYear(selectedDate);
  
  const monthTasks = useMemo(() => {
    return getTasksByMonth(currentYear, currentMonth);
  }, [tasks, currentYear, currentMonth, getTasksByMonth]);

  const tasksByDate = useMemo(() => {
    const grouped = {};
    monthTasks.forEach((task) => {
      if (!grouped[task.date]) {
        grouped[task.date] = [];
      }
      grouped[task.date].push(task);
    });

    // Apply filters
    const filtered = {};
    Object.entries(grouped).forEach(([date, dateTasks]) => {
      let filteredTasks = dateTasks;

      if (searchTerm) {
        filteredTasks = filteredTasks.filter(
          (t) =>
            t.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
            t.description.toLowerCase().includes(searchTerm.toLowerCase())
        );
      }

      if (priorityFilter !== 'All') {
        filteredTasks = filteredTasks.filter((t) => t.priority === priorityFilter);
      }

      if (filteredTasks.length > 0) {
        filtered[date] = filteredTasks;
      }
    });

    return filtered;
  }, [monthTasks, searchTerm, priorityFilter]);

  const stats = {
    total: monthTasks.length,
    completed: monthTasks.filter((t) => t.status === 'completed').length,
    missed: monthTasks.filter((t) => t.status === 'missed').length,
  };

  const handleAddTask = (e) => {
    e.preventDefault();
    if (taskForm.title.trim()) {
      addTask({
        ...taskForm,
        type: 'monthly',
      });
      setTaskForm({
        title: '',
        description: '',
        priority: 'Medium',
        date: format(new Date(), 'yyyy-MM-dd'),
      });
      setShowTaskModal(false);
    }
  };

  const handlePrevMonth = () => {
    setSelectedDate(new Date(currentYear, currentMonth - 2, 1));
  };

  const handleNextMonth = () => {
    setSelectedDate(new Date(currentYear, currentMonth, 1));
  };

  const toggleDateExpanded = (date) => {
    setExpandedDates((prev) => ({
      ...prev,
      [date]: !prev[date],
    }));
  };

  const sortedDates = Object.keys(tasksByDate).sort();

  return (
    <div className="monthly-tasks-page">
      {/* Month Navigation */}
      <section className="month-navigation">
        <button className="nav-btn" onClick={handlePrevMonth} title="Previous month">
          ‚Üê
        </button>

        <div className="month-display">
          <h2>{format(selectedDate, 'MMMM yyyy')}</h2>
        </div>

        <button className="nav-btn" onClick={handleNextMonth} title="Next month">
          ‚Üí
        </button>
      </section>

      {/* Stats */}
      <section className="month-stats">
        <GradientCard className="stat-card gradient-primary">
          <div className="stat-content">
            <span className="stat-icon">üìä</span>
            <div>
              <p className="stat-label">Total Tasks</p>
              <p className="stat-big-number">{stats.total}</p>
            </div>
          </div>
        </GradientCard>

        <GradientCard className="stat-card gradient-success">
          <div className="stat-content">
            <span className="stat-icon">‚úÖ</span>
            <div>
              <p className="stat-label">Completed</p>
              <p className="stat-big-number">{stats.completed}</p>
            </div>
          </div>
        </GradientCard>

        <GradientCard className="stat-card gradient-danger">
          <div className="stat-content">
            <span className="stat-icon">‚ùå</span>
            <div>
              <p className="stat-label">Missed</p>
              <p className="stat-big-number">{stats.missed}</p>
            </div>
          </div>
        </GradientCard>

        <GradientCard className="stat-card gradient-warning">
          <div className="stat-content">
            <span className="stat-icon">‚è≥</span>
            <div>
              <p className="stat-label">Pending</p>
              <p className="stat-big-number">{stats.total - stats.completed - stats.missed}</p>
            </div>
          </div>
        </GradientCard>
      </section>

      {/* Filters and Search */}
      <section className="filters-section">
        <div className="search-box">
          <input
            type="text"
            placeholder="üîç Search tasks..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
        </div>

        <div className="filter-buttons">
          {['All', 'High', 'Medium', 'Low'].map((priority) => (
            <button
              key={priority}
              className={`filter-btn ${priorityFilter === priority ? 'active' : ''}`}
              onClick={() => setPriorityFilter(priority)}
            >
              {priority}
            </button>
          ))}
        </div>

        <button
          className="add-task-btn-main"
          onClick={() => setShowTaskModal(true)}
        >
          ‚ûï Add Task
        </button>
      </section>

      {/* Tasks by Date */}
      <section className="tasks-by-date">
        {sortedDates.length === 0 ? (
          <EmptyState
            icon="üì≠"
            title={searchTerm || priorityFilter !== 'All' ? 'No matching tasks' : 'No tasks this month'}
            message="Add a task to get started!"
          />
        ) : (
          <div className="date-groups">
            {sortedDates.map((date) => (
              <div key={date} className="date-group">
                <button
                  className="date-header"
                  onClick={() => toggleDateExpanded(date)}
                >
                  <span className="date-label">{format(new Date(date), 'EEEE, MMMM dd')}</span>
                  <span className="task-count">{tasksByDate[date].length} tasks</span>
                  <span className={`expand-icon ${expandedDates[date] ? 'expanded' : ''}`}>
                    ‚ñº
                  </span>
                </button>

                {expandedDates[date] && (
                  <div className="date-tasks">
                    {tasksByDate[date].map((task) => (
                      <GradientCard key={task.id} className={`task-card task-${task.status}`}>
                        <div className="task-content">
                          <div className="task-left">
                            <input
                              type="checkbox"
                              checked={task.status === 'completed'}
                              onChange={() => toggleTask(task.id)}
                              className="task-checkbox"
                            />
                            <div className="task-info">
                              <h4 className={task.status === 'completed' ? 'completed-text' : ''}>
                                {task.title}
                              </h4>
                              {task.description && (
                                <p className="task-desc">{task.description}</p>
                              )}
                            </div>
                          </div>

                          <div className="task-right">
                            <PriorityBadge priority={task.priority} />
                            <span className={`status-badge status-${task.status}`}>
                              {task.status}
                            </span>

                            <div className="task-actions">
                              <button
                                className="action-btn miss-btn"
                                onClick={() => markTaskMissed(task.id)}
                                title="Mark as missed"
                              >
                                ‚úï
                              </button>
                              <button
                                className="action-btn delete-btn"
                                onClick={() => deleteTask(task.id)}
                                title="Delete task"
                              >
                                üóëÔ∏è
                              </button>
                            </div>
                          </div>
                        </div>
                      </GradientCard>
                    ))}
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </section>

      {/* Add Task Modal */}
      {showTaskModal && (
        <div className="modal-overlay" onClick={() => setShowTaskModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>Add Task for {format(selectedDate, 'MMMM yyyy')}</h3>
              <button
                className="modal-close"
                onClick={() => setShowTaskModal(false)}
              >
                ‚úï
              </button>
            </div>
            <form onSubmit={handleAddTask} className="modal-form">
              <div className="form-group">
                <label>Task Title *</label>
                <input
                  type="text"
                  value={taskForm.title}
                  onChange={(e) =>
                    setTaskForm({ ...taskForm, title: e.target.value })
                  }
                  placeholder="Enter task title"
                  required
                  autoFocus
                />
              </div>
              <div className="form-group">
                <label>Description</label>
                <textarea
                  value={taskForm.description}
                  onChange={(e) =>
                    setTaskForm({ ...taskForm, description: e.target.value })
                  }
                  placeholder="Enter task description"
                  rows="3"
                />
              </div>
              <div className="form-group">
                <label>Date</label>
                <input
                  type="date"
                  value={taskForm.date}
                  onChange={(e) =>
                    setTaskForm({ ...taskForm, date: e.target.value })
                  }
                  required
                />
              </div>
              <div className="form-group">
                <label>Priority</label>
                <select
                  value={taskForm.priority}
                  onChange={(e) =>
                    setTaskForm({ ...taskForm, priority: e.target.value })
                  }
                >
                  <option>Low</option>
                  <option>Medium</option>
                  <option>High</option>
                </select>
              </div>
              <button type="submit" className="submit-btn">
                Add Task
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default MonthlyTasksPage;
