import React, {useState} from 'react';

export default function SkillPlanner({skills,onAdd,onSetPriority}){
  const [name,setName] = useState('');

  const submit = (e)=>{e.preventDefault(); if(!name) return; onAdd({name}); setName('')}

  return (
    <div className="card">
      <h3>Skill Planner</h3>
      <form onSubmit={submit} style={{display:'flex',gap:8}}>
        <input placeholder="Add skill" value={name} onChange={e=>setName(e.target.value)} />
        <button type="submit">Add</button>
      </form>
      <div style={{marginTop:8}}>
        {skills.map((s,idx)=> (
          <div key={s.id} style={{display:'flex',alignItems:'center',justifyContent:'space-between',padding:'6px 0'}}>
            <div>{idx+1}. {s.name}</div>
            <div>
              <button onClick={()=>onSetPriority(s.id,'up')}>↑</button>
              <button onClick={()=>onSetPriority(s.id,'down')}>↓</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  )
}
