import React from 'react';
import { displayDate } from '../utils/dateUtils';

export default function TaskItem({task,onToggle,onFail,onDelete,onEdit}){
  return (
    <div className="task card">
      <div>
        <h4>{task.title} <span className={`muted`}>[{task.priority}]</span></h4>
        {task.description && <div className="muted">{task.description}</div>}
        <div className="muted">Planned: {displayDate(task.date)}</div>
      </div>
      <div className="task-controls">
        <button onClick={()=>onToggle(task.id)}>{task.status==='completed'?'✔':'Mark ✔'}</button>
        <button onClick={()=>onFail(task.id)}>{task.status==='missed'?'✖':'Mark ✖'}</button>
        <button onClick={()=>onEdit(task.id)}>Edit</button>
        <button onClick={()=>onDelete(task.id)}>Delete</button>
      </div>
    </div>
  )
}
